/**
 * Muestra los productos de la categoría seleccionada
 * @param {string} category - Categoría seleccionada
 */
function showProducts(category) {
  const container = document.getElementById('productContainer');
  container.innerHTML = ''; // Limpia productos anteriores

  // Itera sobre cada producto de la categoría
  products[category].forEach(product => {
    // Crea cada tarjeta con la información del producto
    const card = document.createElement('div');
    card.classList.add('product-card');

    card.innerHTML = `
  <img src="${product.imagen}" alt="${product.nombre}">
  <h3>${product.nombre}</h3>
  <p>${product.descripcion}</p>
  <div class="price">$${product.precio.toLocaleString()}</div>
  <button onclick='addToCart(${JSON.stringify(product)})'>Agregar al carrito</button>
`;

    container.appendChild(card); 
  });
}

// Agrega eventos a los botones de categoría
document.querySelectorAll('.category-btn').forEach(btn => {
  btn.addEventListener('click', () => {
    const category = btn.getAttribute('data-category');
    showProducts(category);
  });
});
/**
 * Agrega un producto al carrito y actualiza la vista
 * @param {Object} product - Producto a agregar
 */
function addToCart(product) {
  cart.push(product); // Agrega al array
  renderCart();       // Actualiza el carrito visualmente
}

